document.addEventListener('DOMContentLoaded', function() {
    // Create a sound manager to handle audio
    const SoundManager = {
        // Sound collection
        sounds: {
            click: {
                src: 'data:audio/mp3;base64,',
                volume: 0.3
            },
            sparkle: {
                src: 'data:audio/mp3;base64,',
                volume: 0.2
            },
            pop: {
                src: 'data:audio/mp3;base64,',
                volume: 0.2
            },
            typing: {
                src: 'data:audio/mp3;base64,',
                volume: 0.1
            },
            notification: {
                src: 'data:audio/mp3;base64,',
                volume: 0.3
            },
            rabbit: {
                src: 'data:audio/mp3;base64,',
                volume: 0.15
            }
        },
        
        // Audio objects
        audioElements: {},
        
        // Initialize the audio elements
        init: function() {
            // Create audio elements for each sound
            for (const sound in this.sounds) {
                this.audioElements[sound] = new Audio(this.sounds[sound].src);
                this.audioElements[sound].volume = this.sounds[sound].volume;
            }
        },
        
        // Track user interaction
        hasInteracted: false,
        
        // Play a sound
        play: function(soundName) {
            // Skip sound playback if user hasn't interacted yet
            if (!this.hasInteracted) {
                return;
            }
            
            try {
                if (this.audioElements[soundName]) {
                    // Reset sound to beginning
                    this.audioElements[soundName].currentTime = 0;
                    this.audioElements[soundName].play().catch(e => {
                        // Just silently ignore autoplay errors
                        // These are expected on first page load
                    });
                }
            } catch (e) {
                // Silently ignore errors
            }
        }
    };
    
    // Initialize sound manager
    SoundManager.init();
    
    // Set up user interaction tracking
    document.addEventListener('click', function() {
        SoundManager.hasInteracted = true;
    });
    
    document.addEventListener('keydown', function() {
        SoundManager.hasInteracted = true;
    });
    
    // Make the sound manager globally available
    window.SoundManager = SoundManager;
    
    // Add sound effects to various interactions
    
    // Play click sound on button clicks
    document.querySelectorAll('.btn').forEach(button => {
        button.addEventListener('click', function() {
            SoundManager.play('click');
        });
    });
    
    // Play sparkle sound on hover for nav links
    document.querySelectorAll('.nav-link').forEach(link => {
        link.addEventListener('mouseenter', function() {
            SoundManager.play('sparkle');
        });
    });
    
    // Play pop sound when text is clicked
    document.querySelectorAll('.message, .compliment, .birthday-message, h1, h2, h3').forEach(element => {
        element.addEventListener('click', function() {
            SoundManager.play('pop');
        });
    });
    
    // Play typing sound for typing effect (integrate with typing-effect.js)
    document.addEventListener('typing-character', function() {
        SoundManager.play('typing');
    });
    
    // Add a custom event to typing-effect.js to trigger the typing sound
    const originalTypeNextCharacter = window.typeNextCharacter;
    if (originalTypeNextCharacter) {
        window.typeNextCharacter = function() {
            originalTypeNextCharacter();
            
            // Dispatch event for typing a character
            document.dispatchEvent(new CustomEvent('typing-character'));
        };
    }
    
    // Play notification sound when chat is opened
    const chatButton = document.getElementById('chat-button');
    if (chatButton) {
        chatButton.addEventListener('click', function() {
            if (document.getElementById('chat-container').classList.contains('d-none')) {
                SoundManager.play('notification');
            }
        });
    }
});